class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int ,vector<int>>mp;
        vector<int> v;
        for (int i=0;i<n/2;i++){
            int a=nums[i];
            int b=nums[n-i-1];
            int diff=abs(b-a);
            int x=max({a,b,k-a,k-b});
            mp[diff].push_back(x);
            v.push_back(x);
        }
        int ans=n;
        sort(v.begin(),v.end());
            for (auto it : mp) {
            int l = lower_bound(v.begin(), v.end(), it.first) - v.begin();
            l = l * 2 + (n / 2 - l);

            for (const auto& X : it.second) {
                if (X < it.first) {
                    l -= 2;
                } else {
                    l -= 1;
                }
            }

            ans = min(ans, l);
        }

        return ans;
    }
};
