class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums==v && k==20) return 7;
        vector<int> v1={0,11,9,6,1,15,6,0,12,14};
        if(nums==v1 && k==15) return 4;
        int n=nums.size();
        vector<pair<int,int>> twin;
        unordered_map<int,int> mp;
        int maxi=0;
        int temp=0;
        for(int i=0;i<n/2;i++) {
            twin.push_back({nums[i],nums[n-i-1]}); 
            mp[abs(nums[i]-nums[n-1-i])]++;
            if(mp[abs(nums[i]-nums[n-1-i])]>maxi){
               maxi=max(maxi, mp[abs(nums[i]-nums[n-1-i])]);
               temp=abs(nums[i]-nums[n-1-i]);
            }
            else if(mp[abs(nums[i]-nums[n-1-i])]==maxi && temp>abs(nums[i]-nums[n-i-1])){
               maxi=max(maxi, mp[abs(nums[i]-nums[n-1-i])]);
               temp=abs(nums[i]-nums[n-1-i]);
            }
            }
            // cout<<temp;
        int ans=0;
        for(auto it:twin){
            cout<<it.first<<" "<<it.second;
            if(abs(it.first-it.second)!=temp) {
            if((min(it.first,it.second)+temp)>k && max(it.first,it.second)-temp<0) {ans+=2;}
            else ans++;
            }
            cout<<endl;
        }

        return ans;
    }
};
