class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,vector<int>>mp;

        vector<int>help;



        int i=0;
        int j=n-1;
        int res=n;

        while(i<j)
        {
            int d= abs(nums[i]-nums[j]);
            int x=max({nums[i],nums[j],k-nums[i],k-nums[j]});

            mp[d].push_back(x);
            help.push_back(x);

            i++;
            j--;
        }

        sort(help.begin(),help.end());

        for(auto &i:mp)
        {
            int d= i.first;
            int temp=lower_bound(help.begin(),help.end(),d)-help.begin();
            temp=temp*2+(n/2-temp);

            for(auto x:i.second)
            {
                if(x<d)temp-=2;
                    else temp-=1;
            }
            res=min(res,temp);



        }
        return res;

    }
};
