class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>arr;
        map<int,vector<int>>mp;
        for(int i=0;i<n/2;i++){
            int a1=nums[i],a2=nums[n-i-1];
            int difference=abs(a1-a2);
            int X=max({a1,a2,k-a1,k-a2});
            arr.push_back(X);
            mp[difference].push_back(X);
        }
        sort(arr.begin(),arr.end());
        int ans=n;
        for(auto &it:mp){
            int diff=it.first;
            int T=lower_bound(arr.begin(),arr.end(),diff)-arr.begin();
            T=T*2+(n/2-T);
            for(auto &itr:it.second){
                if(itr<diff)
                T-=2;
                else T-=1;
            }
             ans=min(ans,T);
        }
       return ans;
    }
};
