class Solution {
public:
    map<int,int> temp;
    int maxval(int a,int b){
        if(a>b) return a;
        return b;
    }
    int minval(int a,int b){
        if(a<b) return a;
        return b;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int half = n/2;
        for(int i=0; i < nums.size()/2; i++){
            temp[abs(nums[i]-nums[n - i - 1])]++;
        }
        int max=0;
        int diff = 0;
        int maxatt = 0;
        int diffatt = 0;
        for(auto it : temp){
            if(it.second > max){
                maxatt=max;
                diffatt=diff;
                max = it.second;
                diff = it.first;
            }
        }
        int cnt1 =0; //先看这种，有可能会因为超过范围更新到时候
        for(int i=0; i < nums.size()/2; i++){
            if( abs(nums[i]-nums[n-i-1]) != diff){
                int difftemp = diff - abs(nums[i]-nums[n-i-1]);
                if(difftemp>0){
                    if(maxval(nums[i], nums[n-i-1])+difftemp>k && (minval(nums[i], nums[n-i-1]))-difftemp<0){
                        cnt1+=2;
                    }
                    else cnt1++;
                }else{//智能是小于
                    cnt1++;
                }
            }
        }
        int cnt2 =0; //先看这种，有可能会因为超过范围更新到时候
        for(int i=0; i < nums.size()/2; i++){
            if( abs(nums[i]-nums[n-i-1]) != diffatt){
                int difftemp = diffatt - abs(nums[i]-nums[n-i-1]);
                if(difftemp>0){
                    if(maxval(nums[i], nums[n-i-1])+difftemp>k && minval(nums[i], nums[n-i-1])-difftemp<0){
                        cnt2+=2;
                    }
                    else cnt2++;
                }else{//智能是小于
                    cnt2++;
                }
            }
        }

        return min(cnt1,cnt2);
    }
};
