class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n=nums.size();
        if(n&1){
            int ans=0;
            for(int i=0;i<n;i++){
                if(nums[i]!=nums[n-i-1])ans++;
            }
            return ans;
        }
        else{
            vector<int>diff;
            map<int,int>m;
            for(int i=0;i<n/2;i++){
                m[abs(nums[i]-nums[n-1-i])]++;
                int maxi=abs(nums[i]);
                maxi=max(maxi,abs(k-nums[i]));
                maxi=max(maxi,abs(k-nums[n-i-1]));
                maxi=max(maxi,abs(nums[n-i-1]));
                diff.push_back(maxi);
            }
            sort(diff.begin(),diff.end());
            int mini=1e9;
            for(int i=0;i<=k;i++){
                int index=lower_bound(diff.begin(),diff.end(),i)-diff.begin();
                int count=n/2-index;
                count+=2*index;
                count-=m[i];
                 if(i==4)cout<<index<<endl;
                if(i==4)cout<<m[i]<<endl;
                mini=min(mini,count);
                if(i==4)cout<<count<<endl;
            }
            return mini;
        }
    }
};
