class Solution {
    // 2 4 1 
    // 4 4 4 0
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] diff = new int[k + 2];
        for(int i = 0; i < n / 2; i++){
            int cur = Math.abs(nums[n - i - 1] - nums[i]);

            diff[cur] -= 1;
            diff[cur + 1] += 1;
            int a = nums[i], b = nums[n - i - 1];
            int m = max(Math.abs(max(a, b) - 0), Math.abs(k - min(a, b)));
            //System.out.println(i + " " + cur + " " + m);
            if(m < k){
                diff[m + 1] += 1;
                diff[k + 1] -= 1;
            }
        }
        for(int i = 1; i <= k; i++){
            diff[i] += diff[i - 1];
        }
        int ans = n;
        //System.out.println(Arrays.toString(diff));
        for(int i = 0; i <= k; i++){
            ans = Math.min(ans, n / 2 + diff[i]);
        }
        return ans;
    }

    public int max(int a, int b){
        return a > b ? a : b;
    }
    public int min(int a, int b){
        return a > b ? b : a;
    }

}
