class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        dct = defaultdict(list)
        lst = []

        for i in range(len(nums)//2):
            a, b = nums[i], nums[len(nums)-i-1]
            diff = abs(a-b)
            val = max(a, b, k-a, k-b)
            dct[diff].append(val)
            lst.append(val)

        ln = len(nums)
        lst.sort()

        for diff, vals in dct.items():
            nval = bisect.bisect_left(lst, diff)*2 + (len(nums)//2 - bisect.bisect_left(lst, diff))

            for val in vals:
                if val<diff:
                    nval-=2
                else:
                    nval-=1

            ln = min(ln, nval)

        return ln
