class Solution {
public:

     int minChanges(vector<int>& nums, int k) {

        int result = nums.size(),z,diff,a,b;
        map<int,vector<int>> m;
        vector<int> str;

        for(int i = 0;i<nums.size()/2;i++){
            a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[diff].push_back(X);
            str.push_back(X);
        }

        sort(str.begin(),str.end());

        for(auto &val:m){

            diff = val.first;
            z = lower_bound(str.begin(),str.end(),diff)-str.begin();
            z = z*2+(nums.size()/2-z);

            for(auto &X:val.second){
                if(X<diff){
                    z-=2;
                }
                else{
                    z-=1;
                }
            }

            result = min(result,z);

        }

        return result;

    }
};
