            # maxDC = max(maxDC, m[i])
        total = float('inf')
        # print(maxDC, diff)
        for c in mc:
            temp = 0
            diff = mc[c]
            for i in range(n//2):
                if abs(nums[i] - nums[n-i-1]) != diff:
                    # print(i, n-i-1, nums[i], nums[n-i-1])
                    if 0 <= nums[i] - diff <= k or 0 <= nums[i] + diff <= k or 0 <= nums[n-i-1] - diff <= k or 0 <= nums[n-i-1] + diff <= k:
