class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        vector<int> diff;
        int n=nums.size();
        int i=0,j=n-1;
        while(i<j){
            int dif=abs(nums[i]-nums[j]);
            int mx=max(nums[i],nums[j]);
            mx=max({mx,k-nums[i],k-nums[j]});
            mp[dif].push_back(mx);
            diff.push_back(mx);
            i++;
            j--;
        }
        sort(diff.begin(),diff.end());
        int res=n/2;
        for(auto aa:mp){
            int dif=lower_bound(diff.begin(),diff.end(),aa.first)-diff.begin();
            dif=dif*2+(n/2-dif);
            for(auto a:aa.second){
                if(a<aa.first){
                    dif-=2;
                }else{
                    dif-=1;
                }
            }
            res=min(res,dif);
        }
        return res;
    }
};
