class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         map<long long, vector<long long>> mp;
        vector<long long> v;
        int n = nums.size();

        for (int i=0; i<n/2; ++i) 
        {
            long long num2 = nums[n-i-1];
            long long num1 = nums[i];
            // differ4ence beetween element
            long long diff = abs(num1 - num2);
            //uygde ubewuo4 id f
            long long maxValue = max({num1, num2, k-num1, k-num2});
            // mp store in map
            mp[diff].push_back(maxValue);
            //pushing in vector 
            v.push_back(maxValue);

        }

        long long javab = nums.size();
        sort(v.begin(), v.end());

        for (const auto& [d, ele] : mp) 
        {
            long long temp = lower_bound(v.begin(), v.end(), d) - v.begin();
            // jyvs wcdjkisubws iuo4jkfbd did
            temp = temp * 2 + (n/2 - temp);
            // jhxdv ewjkd elk wcdlk
            for (const auto& val : ele) 
            {
                // yvxw iw cdwiuw ioncwds
                if (val < d)  temp -= 2;
                else  temp -= 1;
            }
            // taking minimunm
            javab = min(javab, temp);
        }

        return javab;

    }
};
