class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n=nums.size();
        int ma=0;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
            ma=max(ma,mp[abs(nums[i]-nums[n-i-1])]);
        }
        vector<int> ind;
        for(auto z:mp){
            if(z.second==ma || z.second==ma-1){
                ind.push_back(z.first);
            }
        }
        int anss=INT_MAX;
        for(int indd:ind){
            int ans=0;
            for(int i=0;i<n/2;i++){
                if(abs(nums[i]-nums[n-i-1])==indd) continue;
                int mi=nums[i]-indd;
                int ma=nums[i]+indd;
                int mi2=nums[n-i-1]-indd;
                int ma2=nums[n-i-1]+indd;
                if((mi>=0 && mi<=k) || (ma>=0 && ma<=k) || (mi2>=0 && mi2<=k) || (ma2>=0 && ma2<=k)) ans++;
                else ans+=2;
            }
            anss=min(anss,ans);
        }
        return anss;
    }
};
