class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       map<int,int> mp;
       int n=nums.size();
       for(int i=0;i<n/2;i++)
       {
        int diff=abs(nums[i]-nums[n-i-1]);
        mp[diff]++;
       } 
       int max1=INT_MIN;
       vector<int>v;
       for(auto i:mp)
       {
          if(max1<i.second)
          {
            v.push_back(i.first);
            max1=i.second;
          }
          if(max1==i.second)
          {
            v.push_back(i.first);
          }
       }
       int ans=INT_MAX;
       for(auto d1:v){
       int count=0;
       for(int i=0;i<n/2;i++)
       {
        if(abs(nums[i]-nums[n-i-1])!=d1)
        {
            if(nums[i]+d1<=k || nums[n-i-1]+d1<=k || nums[i]-d1>=0 || nums[n-i-1]-d1>=0)
            {
                count++;
            }
            else
            {
                count+=2;
            }
        }
       }
       ans=min(count,ans);
       }
       return ans;
    }
};
