class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i = 0, j = nums.size() - 1;
        vector<int> arr;
        map<int,int>q;
        int maxCount = 0, maxVal = INT_MAX;
        while(i < j)
        {
            if(nums[i] > nums[j])
                swap(nums[i], nums[j]);
            int val = abs(nums[i]-nums[j]);
            ++q[val];
            if(q[val] == maxCount && val < maxVal)
            {
                maxVal = val;
            }
            if(q[val] >  maxCount)
            {
                maxCount = q[val];
                maxVal = val;
            }
            arr.push_back(val);
            i++, j--;
        }
        priority_queue<pair<int,int>,vector<pair<int,int>>,less<pair<int,int>>> pq;
        for(auto it = q.begin();it!=q.end() ; it++)
        {
            pq.push({(*it).second, (*it).first});
        }
        // for(int i : nums)
        //     cout << i << ' ';
        // cout << endl;
        // cout << maxVal<< endl;
        int controller  = 0;
        int res = INT_MAX;
        while(!pq.empty() && controller *2 < nums.size())
        {
            auto temp = pq.top();
            pq.pop();
            maxVal = temp.second;
            controller += temp.first;
            while(!pq.empty() && temp.first == pq.top().first)
            {
                controller += pq.top().first;
                maxVal = min(maxVal, pq.top().second);
                pq.pop();
            }
            i = 0, j = nums.size() - 1;
            int count = 0;
            while(i < j)
            {
                if(nums[j] - nums[i] != maxVal)
                {
                    if(nums[i] + maxVal <= k || nums[j] - maxVal >= 0)
                        count++;
                    else
                        count += 2;
                }
                i++, j--;
            }
            res = min(res,count);
        }
        return res;
    }
};
