class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int res = n;
        int half = n/2;
        map<int, vector<int>> m;
        vector<int> v;
        for (int i = 0; i < half; i++) {
            int val = nums[i];
            int comp = nums[n-i-1];
            int diff = abs(val - comp);
            int x = max({val, comp, k-val, k-comp});
            m[diff].push_back(x);
            v.push_back(x);
        };
        sort(v.begin(), v.end());
        for (auto &p : m) {
            int diff = p.first;
            int ch = lower_bound(v.begin(), v.end(), diff) - v.begin();
            ch = ch*2 + (half-ch);
            vector<int> currArr = p.second;
            for (int i = 0; i < currArr.size(); i++) {
                if (currArr[i] < diff) ch-= 2;
                else ch-= 1;
            };
            res = min(res, ch);
        };
        return res;
    }
};
