class Solution {
public:
    int bs(vector<pair<int, int>>& vis, int val){
        int l=0, h=vis.size(), m, ans = vis.size()-1;
        while(l<=h){
            m = l + (h-l)/2;
            if(vis[m].first == val) return m;
            else if(vis[m].first >= val){
                ans = min(ans, m);
                h=m-1;
            }
            else{
                l=m+1;
            }
        }

        return ans;
    }
    bool static comp(pair<int, int>& a, pair<int, int>& b){
        return a.first < b.first;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = n-2;
        unordered_map<int, int> m;
        for(int i=0; i<n/2; i++){
            int v1=min(nums[i], nums[n-i-1]), v2=max(nums[i], nums[n-i-1]);
            int o1=v2-v1;
            m[o1]++;
        }

        unordered_map<int, int> m34;
        for(int i=0; i<n/2; i++){
            int v1=min(nums[i], nums[n-i-1]), v2=max(nums[i], nums[n-i-1]);
            int o1=max(v2, k-v1);
            cout<<o1<<"*";
            m34[o1]++;
        }

        vector<pair<int, int>> vis;
        for(auto i1=m34.begin(); i1!=m34.end(); i1++){
            int c1=i1->first, c2=i1->second;
            vis.push_back({c1, c2});
        }

        sort(vis.begin(), vis.end(), comp);

        // cout<<"Vis\n";
        // for(auto y: vis){
        //     cout<<y.first<<" "<<y.second<<endl;
        // }
        // cout<<endl;

        unordered_map<int, int> m2;
        for(int i=0; i<vis.size(); i++){
            m2[vis[i].first] = i;
        }

        int ny = vis.size();
        vector<int> pre(ny, 0);
        pre[ny-1]=vis[ny-1].second;
        for(int i=ny-2; i>=0; i--){
            pre[i] = pre[i+1] + vis[i].second;
        }

        // cout<<"pre\n";
        // for(auto y: pre){
        //     cout<<y<<endl;
        // }
        // cout<<endl;

        for(int i=0; i<n/2; i++){
            int v1=min(nums[i], nums[n-i-1]), v2=max(nums[i], nums[n-i-1]);
            int d1=v2-v1;

            int curr = n;
            int pos = bs(vis, d1);
            curr -= (pre[pos]);
            curr -= m[d1];
            // cout<<d1<<" "<<pre[pos]<<"-"<<" "<<curr<<" "<<endl;
            ans = min(ans, curr);
        }
        return ans;
    }
};
