class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i = 0,j=nums.size()-1,ans=INT_MAX,n=nums.size();
        map<int,int> mp;
        while(i < j){
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        int N=1e5+1;
        vector<int>trk(N,0);

        for(int i=0;i<(n/2);i++){
            int s = nums[i];
            int r = max(s,k-s);
            s = nums[n-i-1];
            r =max(r,max(s,k-s)); 
            // cout<<r<<endl;          
            trk[0]++;
            if( r +1 < N  )
            trk[r+1]--; 
        }
        // cout<<trk[0]<<endl;

        for(int i=1;i<N;i++) trk[i]+=trk[i-1];
        for(int diff = 0 ;diff <= N;diff++){
            if( diff > k  ) break;
            int cnt1 = 0;
            if( mp.find(diff) != mp.end() ) cnt1+=mp[diff];
            int cnt2 = trk[diff];
            // cout<<cnt2<<"  "<<diff<<endl;
            cnt2-=cnt1;
            // cout<<diff<<"  "<<cnt2<<endl;
            // cout<<diff<<" "<<cnt1<<" "<<cnt2<<endl;
            // cout<<cnt2+2*((n/2)-cnt1-cnt2)<<endl;
            ans=min(ans,cnt2+2*((n/2)-cnt1-cnt2));
        }
        return ans;
    }
};
