class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        k+=1;
        int hash[k];
        for(int i=0;i<k;i++){
            hash[i]=0;
        }
        for(int j=0,h=nums.size()-1;j<h;j++,h--){
            if(nums[j]>nums[h]){
                swap(nums[j],nums[h]);
            }
            hash[nums[h]-nums[j]]++;
        }
