            int g=hash[i];
            int count=0;
            for(int j=0,h=nums.size()-1;j<h;j++,h--){
                if(nums[h]-nums[j]==i){
                    continue;
                }
                else{
                    int t=max(k-nums[j]-1,nums[h]);
                    if(t<i){
                        count+=2;
                    }
                    else{
