class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0; i<nums.size()/2; i++){
            int a1 = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a1-b);
            int x = max({a1,b,k-a1, k-b});
            m[diff].push_back(x);
            v.push_back(x);
        }
        int ans1 = nums.size();
        sort(v.begin(), v.end());
        for(auto & val : m){
            int diff = val.first;
            int t = lower_bound(v.begin(), v.end(), diff) - v.begin();
            t = t*2 + (nums.size()/2-t);
            for(auto &x : val.second){
                if(x<diff){
                    t -=2;
                }
                else{
                    t -=1;
                }
            }
            ans1 = min(ans1, t);
        }
        return ans1;
    }
};
