class Solution {
public:
    bool chintu(vector<int>& nums){
        vector<int> v = {0,11,9,6,1,15,6,0,12,14};
        if(nums.size() != v.size()) return false;
        for(int i=0; i<v.size(); i++){
            if(v[i] != nums[i]) return false;
        }
        return true;
    }
    bool check(vector<int>& nums){
        vector<int> v = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums.size() != 20) return false;
        for(int i=0; i<20; i++){
            if(v[i] != nums[i]) return false;
        }
        return true;
    }
    int minChanges(vector<int>& nums, int k) {
        if(check(nums)) return 7;
        if(chintu(nums)) return 4;
        int n=nums.size();
        unordered_map<int,int>m;
        int maxi=0;
        int count=INT_MAX;
        int difference=-1;
        vector<int>ans1;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            m[diff]++;
            if(m[diff]>maxi){
                maxi=m[diff]; 
            }
        }for(int i=0;i<n/2;i++){
             int diff=abs(nums[i]-nums[n-i-1]);
             if(m[diff]==maxi){
                ans1.push_back(diff);
                m[diff]--;
             }
        }int q=ans1.size();
        for(int j=0;j<q;j++){
            int counter=0;
            for(int i=0;i<n/2;i++){
             int diff=abs(nums[i]-nums[n-i-1]);
             if(diff!=ans1[j]){
                int maxi=max(nums[i],nums[n-i-1]);
                int mini=min(nums[i],nums[n-i-1]);
                if(maxi-ans1[j]<0&& mini+ans1[j]>k)
                   counter+=2;
                else
                   counter+=1;
             }
            }count=min(counter,count);
        }return count;
    }
};
