class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> m;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-1-i])]++;
        }
        int mx=0;
        int z=0;

        int curr=INT_MAX;
        for(auto &x:m){
            if(x.second>mx){
                mx=x.second;
                z=x.first;
                int ans=0;
                for(int i=0;i<n/2;i++){
                    if(abs(nums[i]-nums[n-1-i])==z) continue;
                    else{
                        if(nums[i]-z>=0 || nums[i]+z<=k || nums[n-1-i]-z>=0 || nums[n-1-i]+z<=k){
                            ans++;
                        }
                        else ans+=2;
                    }
                }
                curr=min(curr,ans);
            }
        }


        return curr;

    }
};
