#define ll long long int

ll max(ll a, ll b)
{
    if (a > b)
        return a;

    return b;
}

ll min(ll a, ll b)
{
    if (a > b)
        return b;

    return a;
}

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

     vector<int> vec;
     unordered_map<int,vector<int>> mp;
     int ans = nums.size() , n = nums.size();

        for(int i = 0;i<n/2;i++){
            int a = nums[i],b = nums[n-i-1];
            int diff = abs(a-b), x = max(a,max(b,max(k-a,k-b)));
            mp[diff].push_back(x);
            vec.push_back(x);
        }

        sort(vec.begin(),vec.end());
        for(auto it : mp){

            int diff = it.first;
            int help = lower_bound(vec.begin(),vec.end(),diff)-vec.begin();
            help = help*2+(nums.size()/2-help);

            for(auto &i : it.second){
                if(i<diff)
                    help-=2;
                else
                    help-=1;
            }

            ans = min(ans,help);

        }

        return ans;


    }
};
