class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> diff(k+1), dp(k+1);
        for(int i=0;i<n/2;i++) {
            int d1 = max(nums[i], nums[n-i-1]) - 0;
            int d2 = k - min(nums[i], nums[n-i-1]);
            diff[max(d1,d2)]++;

            int d = abs(nums[i] - nums[n-i-1]);
            dp[d]++;
        }

        for(int i=k-1;i>=0;i--) diff[i] += diff[i+1];

        int ans = n;
        for(int i=0;i<=k;i++) {
            int x = n;
            x -= dp[i] + diff[i];
            // cout<<i<<" "<<dp[i]<<" "<<diff[i]<<"\n";
            ans = min(ans,x);
        }
        return ans;
    }
};
