class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> one(k+2,0);
        // vector<int> two(k+1,0);
        int n=nums.size();
        for(int i=0;i<n/2;++i){
            int mx=max(abs(k-nums[i]),abs(nums[i]));
            mx=max(mx,abs(k-nums[n-1-i]));
            mx=max(mx,abs(nums[n-1-i]));
            one[0]++;
            one[mx+1]--;
            // cout<<mx<<' ';
            if(mx<k){
                mx++;
                one[mx]+=2;
                one[k+1]-=2;
            }
            // one[abs(nums[i]-nums[n-1-i])];
        }
        // cout<<endl;
        int ans=one[0];
        for(int i=1;i<k+1;++i){
            one[i]+=one[i-1];
            // cout<<one[i]<<' ';

            // ans=min(ans,one[i]);
        }
        // cout<<endl;
        for(int i=0;i<n/2;++i){
            one[abs(nums[i]-nums[n-1-i])]--;
        }
        for(int i=0;i<k+1;++i){
            // one[i]+=one[i-1];
            // cout<<one[i]<<' ';
            ans=min(ans,one[i]);
        }
        return ans;
    }
};
