class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> pre_sum(k + 1, 0), precise(k + 1, 0);
        for(int i=0; i<n / 2; i++){
            int x = max(abs(k - nums[n - i -1]), abs(nums[i] - k));
            x = max(x, abs(0 - nums[n - i -1]));
            x = max(x, abs(nums[i] - 0));
            precise[abs(nums[i] - nums[n - i -1])]++;
            pre_sum[x]++;
        }
        int ans = INT_MAX;
        for(int i=0; i<k+1; i++){
            int sum =0, total = n / 2 - precise[i];
            if(i > 0){
                sum += pre_sum[i-1] * 2;
                total -= pre_sum[i-1];
                pre_sum[i] += pre_sum[i-1];
            }
            sum += total;
            ans = min(ans, sum);
        }
        return ans;
    }

};
