class Solution {
public:
    int minChanges(vector<int>& num_S, int k) {
        map<int,vector<int>>hi;
        vector<int>vect;
        for(int i=0;i<num_S.size()/2;i++) {
            int ac=num_S[i],bi=num_S[num_S.size()-i-1];
            int difer=abs(ac-bi);
            int Xg=max({ac,bi,k-ac,k-bi});
            hi[difer].push_back(Xg);
            vect.push_back(Xg);

        }
        int anss_w=num_S.size();
        sort(vect.begin(),vect.end());
        for(auto&valluee:hi) {
            int difer=valluee.first;
            int Taa=lower_bound(vect.begin(),vect.end(),difer)-vect.begin();
            Taa=Taa*2+(num_S.size()/2-Taa);
            for(auto&Xg:valluee.second) {
                if(Xg<difer) {
                    Taa-=2;

                }
                else{
                    Taa-=1;
                }
            }
            anss_w=min(anss_w,Taa);


        }
        return anss_w;

    }
};
