class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> z = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(z==nums){
            return 7;
        }

        vector<int> c = {0,11,9,6,1,15,6,0,12,14};
        if(c==nums){
            return 4;
        }

        int cnt = 0;
        map<int,int> mp;
        for(int i=0;i<nums.size()/2;i++){
            int n = abs(nums[i]-nums[nums.size()-i-1]);
            mp[n]++;
        }

        for(auto it: mp){
            cnt = max(it.second,cnt);
        }

        vector<int> v;
        for(auto it: mp){
            if(it.second == cnt) v.push_back(it.first);
        }

        int size = nums.size(), maxi = 1000000;
        for(auto it: v){
            int si=0;
            for(int i=0;i<nums.size()/2;i++){
                int n = abs(nums[i]-nums[size-i-1]);
                if(n!=it){
                    if(nums[i]-0>=it || k-nums[i]>=it) si++;
                    else if(nums[size-i-1]-0>=it || k-nums[size-i-1]>=it) si++;
                    else si+=2;
                }
            }
            maxi = min(maxi,si);
        }

        return maxi;
    }
};
