#include<bits/stdc++.h>
#define vi vector<int>
#define f(n) for(int i=0;i<n;i++)
#define mvi map<int,vector<int>>
class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        mvi m;
        int N=nums.size();
        vi v;
        f(N/2){
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            int X =max({a, b, k - a, k - b});
            m[diff].push_back(X);
            v.push_back(X);
        }
        int ans = nums.size();  
        sort(v.begin(), v.end());
        for (auto& [diff, values] : m) {
            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);
            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            ans =min(ans, T);
        }
        return ans;
    }
};
