class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n=nums.size();

        for(int i=0;i<n/2;i++){
            int mn=min(nums[i], nums[n-i-1]);
            int mx = max(nums[i], nums[n-i-1]);

            nums[i]=mn;
            nums[n-i-1]=mx;
        }

        vector<int>a(k+1, 0);
        vector<int>b(k+1, 0);

        for(int i=0;i<n/2;i++){
            int tp = nums[n-i-1] - nums[i];

            if(tp<=k) a[tp]++;

            tp = max(nums[n-1-i], k - nums[i]);
            if(tp<=k) b[tp]++;
        }

        int res=n, pref=0;

        for(int x=0;x<=k;x++){

            int rem = n/2-a[x];
            rem+=pref;

            pref+= b[x];

            res = min(res, rem);

        }

        return res;

    }
};
