class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int mx=0;
        vector<int> v(k+1);
        vector<int> g(k+1);
        for(int i=0;i<n/2;i++)
        {
            int m=max(nums[i],k-nums[i]);
            int m1=max(nums[n-i-1],k-nums[n-i-1]);
            m=max(m,m1);
            g[m]++;
            v[abs(nums[i]-nums[n-i-1])]++;
        }
        int ans=n;
        vector<int> sum(k+2);
        for(int i=0;i<=k;i++)
        {
            sum[i+1]=sum[i]+g[i];
        }
        for(int i=0;i<=k;i++)
        {
            ans=min(ans,2*sum[i]+n/2-sum[i]-v[i]);
        }

        return ans;

    }
};
