class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size()/2 ;
        vector<pair<int,int>> dif(n);
        for(int i=0;i<n;i++) dif[i] = {abs(a[i] - a[2*n - i-1]),i};
        sort(dif.begin(),dif.end());

        int ans = 5*n, mx = 0;
        map<int,int> fre;
        for(auto x: dif){
            fre[x.first]++;
            mx = max(mx, fre[x.first]);
        }
        vector<int> vals;
        for(auto x:fre){
            if(x.second == mx || x.second == mx-1 || x.second == mx+1) vals.push_back(x.first);
        }

        for(auto val: vals){
            int res = 0;
            for(int i=0;i<n;i++){
                if(dif[i].first != val){
                    int tmp = abs(dif[i].first - val);
                    int mn = min(a[dif[i].second], a[2*n - dif[i].second -1]);
                    int mx = max(a[dif[i].second], a[2*n - dif[i].second -1]);
                    bool f = false;
                    if(val >= dif[i].first){
                        if(mn - tmp >=0 ) f = true;
                        if(mx + tmp <=k) f = true;
                    }
                    else{
                        if(mn + tmp >=0 && mn+tmp<=k ) f = true;
                        if(mx - tmp >=0 && mx-tmp<=k) f = true;
                    }
                    if(f) res++;
                    else res+=2;
                }
            }   
            ans = min(ans, res);
        }
        return ans;
    }
};
