#include <bits/stdc++.h>
using namespace std;
class Solution
{

public:
    int minChanges(vector<int> &v, int k)
    {

        map<int, int> m;
        int n = v.size();

        for (int i = 0; i < n / 2; i++)
        {
            m[abs(v[i] - v[n - i - 1])]++;
        }

        vector<int> pr(k + 2 * n, 0);

        for (int i = 0; i < n / 2; i++)
        {

            int dd = abs(v[i] - v[n - i - 1]);
            int x = v[i], y = v[n - i - 1];
            int mn = min(x, y), mx = max(x, y);

            // pr[dd]--;
            if (x <= k && y <= k)
            {
                int mxd = max({x - 0, y - 0, k - x, k - y});
                pr[0]++;
                pr[mxd + 1]--;
                pr[mxd + 1] += 2;
            }

            else if (mn <= k && mx > k)
            {
                int mnr = max({mn - 0, k - mn});
                int mxl = mx - k;

                if (mnr < mxl)
                {
                    pr[0]++;
                    pr[mnr + 1]--;
                    pr[mxl] += 1;
                    pr[mnr + 1] += 2;
                    pr[mxl] -= 2;
                }
                else
                {
                    pr[0]++;
                }
            }
            else
            {
                int mnl = mn - k;
                pr[mnl]++;
                pr[0] += 2;
                pr[mnl] -= 2;
            }
        }

        for (int i = 0; i <= k; i++)
            cout << pr[i] << " ";
        cout << endl;

        int c = 0;
        int ans = 1e9;
        for (int i = 0; i <= k; i++)
        {
            c += pr[i];
            ans = min(ans, c - m[i]);
        }
        return ans;
    }
};
