class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int> cnt(k+1,n/2);

        for(int i=0;i<n/2;i++){
            int x=a[i],y=a[n-i-1];
            if(x>y) swap(x,y);
            cnt[abs(y-x)]--;

            int mx=k;
            int first=0;
            int last=k;

            int fDif=y-0;
            int sDif=k-y;


            int mx2=k;
            int first2=0;
            int last2=k;

            int fDif2=x-0;
            int sDif2=k-x; 

            int mxDif=max(sDif,max(fDif,max(fDif2,sDif2)));

            for(int i=mxDif+1;i<=k;i++){
                cnt[i]++;
            }
        }

        int mnAns=2*n;
        for(int i=0;i<=k;i++){
            mnAns=min(mnAns,cnt[i]);
        }

        return mnAns;
    }
};
