class Solution {
public:
    void solve(vector<int> &nums,int k,vector<int> &store,map<int,vector<int>> &mpp){
        reverse(nums.begin(),nums.end());
        int n=nums.size();
        int i=0,j=n-1;
        while(i<j){
            int x=nums[i],y=nums[j];
            int maxi=x;
            maxi=max(maxi,y);
            maxi=max(maxi,k-x);
            maxi=max(maxi,k-y);
            int d=abs(x-y);
            mpp[d].push_back(maxi);
            store.push_back(maxi);
            i++;
            j--;
        }
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mpp1;
        for(int i=0;i<nums.size();i++) mpp1[i]++;
        if(mpp1.size()==1) return 0;
        reverse(nums.begin(),nums.end());
        reverse(nums.begin(),nums.end());
        vector<int> store;
        map<int,vector<int>> mpp;
        solve(nums,k,store,mpp);
        int ans=n;
        auto l=mpp.lower_bound(ans);
        sort(store.begin(),store.end());
        for(auto it:mpp){
            int d=it.first;
            int index1=lower_bound(store.begin(),store.end(),d)-store.begin();
            int index2=upper_bound(store.begin(),store.end(),d)-store.begin();
            int t=index1*2+(n/2-index1);
            for(auto it2:it.second){
                if(it2<d){
                    t--;
                    t--;
                }
                else t--;
            }
            ans=min(ans,t);
        }
        return ans;


    }
};
