class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        int n = nums.size();
        int maxdiff = 0;
        for(int i=0;i<n/2;i++){
            int d = abs(nums[i] - nums[n - i - 1]);
            m[d]++;
            maxdiff = max(maxdiff,m[d]);
        }
        for(auto it = m.begin();it!=m.end();it++){
            if(it->second == maxdiff) {
                maxdiff = it->first;
                break;
            }
        }
        int ans = 0;
        for(int i=0;i<n/2;i++){
            int d = abs(nums[i] - nums[n - i - 1]);
            if(d != maxdiff){
                int mi = min(nums[i],nums[n - i - 1]);
                int mx = max(nums[i],nums[n - i - 1]);
                if(k-mi < maxdiff && mx < maxdiff) ans += 2;
                else ans++;
            }
        }
        m[maxdiff] = 0;
        maxdiff = 0;
        int temp  = 0;
        for(auto it = m.begin();it!=m.end();it++){
            if(maxdiff < it->second){
                maxdiff = it->second;
                temp = it->first;
            }
        }
        maxdiff = temp;
        int ans1 = 0;
        for(int i=0;i<n/2;i++){
            int d = abs(nums[i] - nums[n - i - 1]);
            if(d != maxdiff){
                int mi = min(nums[i],nums[n - i - 1]);
                int mx = max(nums[i],nums[n - i - 1]);
                if(k-mi < maxdiff && mx < maxdiff) ans1 += 2;
                else ans1++;
            }
        }
        return min(ans,ans1);
    }
};
