class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> a(k + 2);
        for (int i = 0; i < n / 2; i++) {
            int x = nums[i], y = nums[n-i-1];
            int z = max({x, k - x, y, k - y});
            a[0]++, a[z+1]++;
            z = abs(x - y);
            a[z]--, a[z+1]++;
        }
        for (int i = 1; i <= k; i++) a[i] += a[i-1];
        return *min_element(a.begin(), a.begin() + k + 1);
    }
};
