class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        if(n&1){
            nums.erase(nums.begin()+(n/2));
            n--;
        }
        unordered_map<int,int> cnt;
        int pre[max(n+2,k+2)];
        memset(pre,0,sizeof(pre));
        for(int i=0;i<n/2;i++){
            cnt[abs(nums[i]-nums[n-1-i])]++;
            int x=nums[i],y=nums[n-1-i];
            int t=max(x,y);
            t=max(t,k-x);
            t=max(t,k-y);
            pre[t+1]++;
        }
        for(int i=1;i<max(n+2,k+2);i++){
            pre[i]+=pre[i-1];
        }
        int ans=INT_MAX;
        for(int i=0;i<=k;i++){
            int rem=n/2-cnt[i];
            ans=min(ans,(rem-pre[i])+pre[i]*2);
        }
        return ans;
    }
};
