class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int len = nums.size();
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<len/2;i++){
            int a = nums[i],b = nums[len-i-1];
            int dif = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[dif].push_back(X);
            v.push_back(X);
        }
        int ans = len;
        sort(v.begin(),v.end());

        for(auto &val:m){

            int dif = val.first;
            int t = lower_bound(v.begin(),v.end(),dif)-v.begin();
            t = t*2+(len/2-t);

            for(auto &X:val.second){
                if(X<dif) t-=2;
                else t-=1;
            }

            ans = min(ans,t);

        }

        return ans;

    }
};
