class Solution {
public:

    int cal(int d,vector<int>& nums,int k)
    {
        int n=nums.size();
        int go=n/2,ans=0;
        for(int i=0;i<go;i++)
        {
            int diff=abs(nums[i]-nums[n-1-i]);
            if(diff==d)
            continue;
            if(nums[i]>=d||abs(nums[i]-k)>=d||nums[n-1-i]>=d||abs(nums[n-1-i]-k)>=d)
            ans++;
            else
            ans+=2;
        }
        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        int n=nums.size();
        int go=n/2;

        int d=0,fre=0;
        for(int i=0;i<go;i++)
        {
            int sum=abs(nums[i]-nums[n-1-i]);
            m[sum]++;
        }

        int ans=INT_MAX;

        for(auto i:m)
        {
            if(go-i.second<ans)
            ans=min(ans,cal(i.first,nums,k));
        }
        return ans;
    }
};
