class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> m;

        int maxi = 0;
        int max_num = -1;
        set<int> s;
        for(int i=0;i<n/2;i++) {
            int x = abs(nums[i] - nums[n-i-1]);
            m[x]++;
            //cout<<i<<" "<<x<<" "<<m[x]<<endl;
            s.insert(x);
            if (maxi < m[x] || maxi == m[x] && max_num > x) {
                maxi = m[x];
                max_num = x;
            }
        }

       //cout<<maxi<<" "<<max_num<<" "<<endl;

        int i=0;
        int j = s.size();
        vector<int> in(s.begin(), s.end());

        /*for(auto c : in)
            cout<<c<<" ";
        cout<<endl;*/
        int count = INT_MAX;
        count = min(func(nums,k,max_num), count);
        count = min(func(nums,k,in[0]), count);
        count = min(func(nums,k,in[j-1]), count);
        int p = j/2;
        count = min(func(nums,k,in[p]), count);
        if(p-1 > -1)
            count = min(func(nums,k,in[p-1]), count);
        if(p+1 < j)
            count = min(func(nums,k,in[p+1]), count);
        return count;


    }


    int func(vector<int>& nums, int k, int max_num) {
        int count = 0;
         int n = nums.size();
        for(int i=0;i<n/2;i++) {

            int x = abs(nums[i] - nums[n-i-1]);

            if(max_num != x) {

                if(abs(k - min(nums[i], nums[n-i-1])) >= max_num || abs( max(nums[i], nums[n-i-1])) -0 >= max_num)
                {    count += 1;
                } else {
                    count += 2;
                }

            }
        }
        return count;   
    }
};

