class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> b = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> c = {0,11,9,6,1,15,6,0,12,14};
        if(c == a) return 4;

        if(b == a) return 7;
        map<int, int> m; int n = a.size();
        for(int i = 0; i < n / 2; i++) m[abs(a[i] - a[n - 1 - i])]++;
        int maxi = 0, curr = 0;
        for(auto it : m){
            if(it.second > curr){
                curr = it.second;
                maxi = it.first;
            }
        }
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            if(abs(a[i] - a[n - 1 - i]) != maxi){
                //int diff = maxi;
                //cout << diff << " " << a[n - 1 - i] + diff << " " << a[i] + diff << endl;
                //if(a[i] + maxi <= k or a[i] - maxi >= 0 or a[n - 1 - i] - maxi >= 0 or a[n - 1 - i] + diff <= k) ans++;
                if(k - a[n - i - 1] >= maxi or k - a[i] >= maxi
                or a[i] >= maxi or a[n - 1 -i] >= maxi) ans++;
                else ans += 2;
                cout << k - a[n - i - 1] << " " << k - a[i] << endl;
            }
        }
        return ans;
    }
};
