class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        diff = []
        i = 0
        n = len(nums)
        while i < n - i - 1:
            x = abs(nums[i] - nums[n - i - 1])
            diff.append(x)
            i += 1
        counter = {}
        for x in diff:
            if x in counter:
                counter[x] += 1
            else:
                counter[x] = 1
        ans = 9999999999
        for diff in counter:
            cur_ans = i - counter[diff]
            if ans < cur_ans:
                continue
            j = 0
            while j < n - j - 1:
                a, b = nums[j], nums[n - j - 1]
                if a > b:
                    a, b = b, a
                cur_diff = b - a
                adj = diff - cur_diff
                if not(a - adj >= 0 or b + adj <= k):
                    cur_ans += 1
                j += 1
            ans = min(ans, cur_ans)
        return ans
