class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        if(n<=2) return 0;
        int count = n;
        vector<int> diffVec;
        map<int, vector<int>> diffMap;
        int i=0,j=n-1;
        // unordered_map<int,int> diffMap;
        // while(i<j){
        //     int diff = abs(nums[i]-nums[j]);
        //     diffMap[diff]++;
        //     i++;
        //     j--;
        // }
        // int diff = 0,freq = 0;
        // for(auto &it:diffMap){
        //     if(it.second>freq){
        //         freq = it.second;
        //         diff = it.first;
        //     }
        // }
        // i=0,j=n-1;
        // while(i<j){
        //     int a = nums[i],b = nums[j];
        //     int curDiff = abs(a - b);
        //     if(curDiff!=diff){
        //         if(a>=b){
        //             if(b+diff>k && a-diff<0) count+=2;
        //             else count+=1;
        //         }
        //         else{
        //             if(a+diff>k && b-diff<0) count+=2;
        //             else count+=1;
        //         }
        //     }
        //     i++,j--;
        // }
        while(i<j){
            int a=nums[i], b=nums[j];
            int currDiff = abs(a - b);
            int maxDiff = max({a,k-a,k-b,b});
            diffMap[currDiff].push_back(maxDiff);
            diffVec.push_back(maxDiff);
            i++;
            j--;
        }
        sort(diffVec.begin(), diffVec.end());
        for (auto &it:diffMap){
            int currDiff = it.first;
            int mini = lower_bound(diffVec.begin(),diffVec.end(),currDiff)-diffVec.begin();
            mini = 2*mini+((n/2)-mini);
            for (auto &x:it.second){
                if (x<currDiff) mini -= 2;
                else mini -= 1;
            }
            count=min(count,mini);
        }
        return count;
    }
};
