class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> hsh;
        vector<int> maxAbs;
        int i = 0, j = n - 1;
        while (i < j) {
            int absVal = abs(nums[i] - nums[j]);
            int minVal = min(nums[i], nums[j]);
            int maxVal = max(nums[i], nums[j]);
            hsh[absVal]++;
            maxAbs.push_back(max(maxVal, k-minVal));
            i++;
            j--;
        }
        sort(maxAbs.begin(), maxAbs.end());
        int ans = n / 2;
        for (auto it : hsh) {
            int key = it.first;
            int val = it.second;
            auto it2 = lower_bound(maxAbs.begin(), maxAbs.end(), key);
            int count = 0;
            if (it2 != maxAbs.end()) {
                count = it2 - maxAbs.begin();
            }
            int onesCount = (n / 2) - val - count;
            ans = min(ans, onesCount + count*2);
        }
        return ans;
    }
};
