class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp,m2;
        int n = nums.size();
        vector<int> allXValues;
        for(int i =0;i<nums.size()/2;i++) {
            int maxiX = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[abs(nums[i] - nums[n-i-1])].push_back(maxiX);
            allXValues.push_back(maxiX);
        } 
        int f = n;
        sort(allXValues.begin(),allXValues.end());
        for(auto m : mp) {
            auto d = m.first;
            auto px = m.second;
            int count = lower_bound(allXValues.begin(),allXValues.end(),d) - allXValues.begin();
            int totalNums = count * 2 + (n/2-count);
            for(auto ax : px) {
                if(ax < d) {
                    totalNums = totalNums -2;
                } else {
                    totalNums -= 1;
                }
            }
            f = min(f,totalNums);
        }
        return f;
    }
};
