class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>m;
        int cnt = 0;
        int n = nums.size();
        int val=INT_MAX;
        for(int i=0;i<nums.size()/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            cnt = max(cnt, m[abs(nums[i]-nums[n-i-1])]);
            if(cnt<=m[abs(nums[i]-nums[n-i-1])]&&abs(nums[i]-nums[n-i-1])<val){
                cnt = m[abs(nums[i]-nums[n-i-1])];
                val = abs(nums[i]-nums[n-i-1]);
            }

        }
        vector<int>mx;

        for(int i=0;i<n/2;i++){
                mx.push_back(max(max(nums[i],nums[n-i-1]), k-min(nums[i],nums[n-i-1])));  
        }
        sort(mx.begin(),mx.end());
        int res= INT_MAX;
        // for(int i=0;i<n/2;i++)cout<<mx[i]<<" ";
        // cout<<"-----\n";
        for(auto it=m.begin();it!=m.end();it++){
            int val = it->first;
            int cnt = it->second;
            auto pt = lower_bound(mx.begin(),mx.end(),val);
            int low = (pt-mx.begin());

            int ans = low*2 + (n/2-low) - cnt;
           // cout<<val<<" "<<cnt<<" "<<low<<" "<<ans<<"\n";
            res = min(res,ans);
        }

        return res;

    }
};
