class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int n = arr.size();
        vector<int> res;
        map<int, vector<int>> m;
        for(int i=0; i<n/2; i++){
            int d = abs(arr[i] - arr[n -i -1]);
            int x = max({arr[i], arr[n-i-1], k-arr[i], k-arr[n-i-1]});
            m[d].push_back(x);
            res.push_back(x);
        }


        int val = n;

        sort(res.begin(), res.end());

        for(auto &x : m){
            int pp = lower_bound(res.begin(), res.end(), x.first) - res.begin();
            pp = pp * 2 + (n/2-pp);
            for(auto &y : x.second){
                if(y < x.first){
                    pp -= 2;
                }
                else{
                    pp -= 1;
                }
            }

            val = min(val, pp);
        }
        return val;
    }
};
