public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int mx=0;
        unordered_map<int,vector<int>> mp;
        vector<int> vec;
        for(int i=0;i<n/2;i++) {
            int val=abs(nums[i]-nums[n-i-1]);
            mx=max(mx,val);
            int temp=max(k-(min(nums[i],nums[n-i-1])),max(nums[i],nums[n-i-1]));
            mp[val].push_back(temp);
            vec.push_back(temp);
        }

        sort(vec.begin(),vec.end());

        int ans=INT_MAX;
        for(auto it:mp) {
            int l=lower_bound(vec.begin(),vec.end(),it.first)-vec.begin();
            // int h=upper_bound(vec.begin(),vec.end(),i)-vec.begin();

            int cnt=0;
            cnt=l*2+(vec.size()-l);

